import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Modal, Button } from 'react-bootstrap';
import PropTypes from 'prop-types';
import { motion, AnimatePresence } from 'framer-motion';
import { FiX, FiShoppingCart, FiSearch, FiRefreshCw } from 'react-icons/fi';
import './Menu.css';

// Added: Function to get stored items
const getStoredItems = () => JSON.parse(localStorage.getItem('foodItems')) || [];

const foodItems = [
    { id: 1, name: 'Chicken Biryani', category: 'Biryanis', price: '12.99', image: 'data:image/jpeg;base64,' },
    { id: 2, name: 'Mutton Biryani', category: 'Biryanis', price: '15.99', image: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Veg Biryani', category: 'Biryanis', price: '10.99', image: 'data:image/jpeg;base64,' },
    { id: 4, name: 'Egg Biryani', category: 'Biryanis', price: '11.99', image: 'data:image/jpeg;base64,' },

    { id: 5, name: 'Burger', category: 'Fast Foods', price: '8.99', image: 'data:image/jpeg;base64,' },
    { id: 6, name: 'Pizza', category: 'Fast Foods', price: '14.99', image: 'data:image/jpeg;base64,' },
    { id: 7, name: 'Sandwich', category: 'Fast Foods', price: '6.99', image: 'data:image/jpeg;base64,' },
    { id: 8, name: 'Shawarma', category: 'Fast Foods', price: '9.99', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTK4kz5zn16NVlxkoXOgY8aq0qN8DzSCALS_w&s' },

    { id: 9, name: 'Vanilla Ice Cream', category: 'Ice Creams', price: '4.99', image: 'data:image/jpeg;base64,' },
    { id: 10, name: 'Chocolate Ice Cream', category: 'Ice Creams', price: '5.99', image: 'data:image/jpeg;base64,' },
    { id: 11, name: 'Strawberry Ice Cream', category: 'Ice Creams', price: '4.99', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS5m7m1Tecwa-PxhN4bS7MqzuU1FA98bmmmeg&s' },
    { id: 12, name: 'Mango Ice Cream', category: 'Ice Creams', price: '5.49', image: 'data:image/jpeg;base64, ' },

    { id: 13, name: 'Mango Smoothie', category: 'Smoothies', price: '5.99', image: 'data:image/jpeg;base64,' },
    { id: 14, name: 'Banana Smoothie', category: 'Smoothies', price: '5.49', image: 'data:image/jpeg;base64,' },
    { id: 15, name: 'Berry Smoothie', category: 'Smoothies', price: '6.99', image: 'data:image/jpeg;base64,' },
    { id: 16, name: 'Green Smoothie', category: 'Smoothies', price: '7.49', image: 'data:image/jpeg;base64,' },
];
const Menu = ({ addToCart }) => {
    const [filter, setFilter] = useState('All');
    const [searchTerm, setSearchTerm] = useState('');
    const [showPopup, setShowPopup] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const navigate = useNavigate();

    const filteredItems = foodItems.filter(item => {
        const searchLower = searchTerm.toLowerCase();
        const matchesSearch = 
            item.name.toLowerCase().includes(searchLower) ||
            item.category.toLowerCase().includes(searchLower);

        const matchesCategory = filter === 'All' || item.category === filter;
        
        return matchesSearch && matchesCategory;
    });

    const handleAddToCart = (item) => {
        addToCart(item);
        setSelectedItem(item);
        setShowPopup(true);
        setTimeout(() => {
            if (showPopup) navigate('/cart');
        }, 2500);
    };

    const handleClose = () => setShowPopup(false);

    return (
        <div className="menu-page">
            <div className="menu-header">
                <h1 className="page-title">Discover Our Menu</h1>
                
                <div className="search-filter">
                    <div className="search-bar">
                        <FiSearch className="search-icon" aria-hidden="true" />
                        <input
                            type="text"
                            placeholder="Search dishes..."
                            aria-label="Search menu items"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        {searchTerm && (
                            <button 
                                className="clear-search"
                                onClick={() => setSearchTerm('')}
                                aria-label="Clear search"
                            >
                                <FiX />
                            </button>
                        )}
                    </div>
                </div>

                <div className="category-section">
                    <h3 className="section-title">Categories</h3>
                    <div className="filter-buttons">
                        {['All', 'Biryanis', 'Starters', 'Curries', 'Breads', 'Sides', 'Desserts', 'Beverages', 'Street Food', 'Main Course'].map(category => (
                            <button
                                key={category}
                                onClick={() => {
                                    setFilter(category);
                                    setSearchTerm('');
                                }}
                                className={`pill-button ${filter === category ? 'active' : ''}`}
                                aria-label={`Filter by ${category}`}
                            >
                                {category}
                            </button>
                        ))}
                    </div>
                </div>
            </div>

            <div className="menu-items">
            {filteredItems.length > 0 ? (
                    filteredItems.map(item => (
                        <div key={item.id} className="menu-item-card">
                            <div className="item-image">
                                <img 
                                    src={item.image} 
                                    alt={item.name}
                                    loading="lazy"
                                    onError={(e) => {
                                        e.target.src = '/images/fallback-food.jpg'; // Ensure this path is correct
                                    }}
                                />
                            </div>
                            <div className="item-details">
                                <h3 className="item-name">{item.name}</h3>
                                <p className="item-category">{item.category}</p>
                                <div className="item-footer">
                                    <span className="item-price">
                                        {new Intl.NumberFormat('en-US', {
                                            style: 'currency',
                                            currency: 'USD'
                                        }).format(item.price)}
                                    </span>
                                    <button
                                        className="add-to-cart-btn"
                                        onClick={() => handleAddToCart(item)}
                                        aria-label={`Add ${item.name} to cart`}
                                    >
                                        <FiShoppingCart className="cart-icon" />
                                        Add to Cart
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <div className="no-items-found">
                        <img 
                            src="/images/empty-state.svg" 
                            alt="No items found" 
                            aria-hidden="true"
                        />
                        <p>No items found matching "{searchTerm}"</p>
                        {filter !== 'All' && <p>in {filter}</p>}
                        <button
                            className="reset-filters"
                            onClick={() => {
                                setFilter('All');
                                setSearchTerm('');
                            }}
                        >
                            Reset Filters
                        </button>
                    </div>
                )}
            </div>

            {/* Cart Added Modal */}
            <Modal 
                show={showPopup} 
                onHide={handleClose}
                centered
                className="custom-modal"
                backdropClassName="custom-backdrop"
                aria-labelledby="cart-added-modal"
            >
                <div className="modal-content">
                    <Modal.Header closeButton>
                        <Modal.Title className="modal-title">
                            <FiShoppingCart aria-hidden="true" /> Added to Cart!
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body className="modal-body">
                        <div className="added-item">
                            <img 
                                src={selectedItem?.image} 
                                alt={selectedItem?.name}
                                onError={(e) => {
                                    e.target.src = '/images/fallback-food.jpg'; // Ensure this path is correct
                                }}
                            />
                            <div className="item-info">
                                <h4>{selectedItem?.name}</h4>
                                <p>
                                    {selectedItem?.price && 
                                        new Intl.NumberFormat('en-US', {
                                            style: 'currency',
                                            currency: 'USD'
                                        }).format(selectedItem.price)
                                    }
                                </p>
                            </div>
                        </div>
                    </Modal.Body>
                    <Modal.Footer className="modal-footer">
                        <Button 
                            variant="secondary" 
                            onClick={handleClose}
                            className="modal-btn"
                            aria-label="Continue shopping"
                        >
                            Continue Shopping
                        </Button>
                        <Button 
                            variant="primary" 
                            onClick={() => navigate('/cart')}
                            className="modal-btn"
                            aria-label="View cart"
                        >
                            View Cart
                        </Button>
                    </Modal.Footer>
                </div>
            </Modal>
        </div>
    );
};

Menu.propTypes = {
    addToCart: PropTypes.func.isRequired
};

export default Menu;